function fetchProxy(method, endpoint, data) {
    const opts = { method, headers: {} };
    if (data) {
        opts.headers['Accept'] = 'application/json';
        opts.headers['Content-Type'] = 'application/json';
        opts.body = JSON.stringify(data);
    }
    return (fetch(endpoint, opts).then(r => r.json()));
};

export function getProxy(endpoint) {
    return (fetchProxy('GET', endpoint));
};

export function postProxy(endpoint, data) {
    return(fetchProxy('POST', endpoint, data));
};

export function putProxy(endpoint, data) {
    return (fetchProxy('PUT', endpoint, data));
}

export function delProxy(endpoint) {
    return (fetchProxy('DELETE', endpoint));
}